                }
                this.input_value = new_value;
                if (this.value !== new_value) {
                    this.$emit('input', new_value);
                }


            },
            value(new_value) {
                this.value = new_value;
                if (this.input_value !== new_value) {
                    this.input_value = new_value;
                    this.initFontPicker(this.icon_type)
                }

            }
        },
        methods:{
            initFontPicker(type) {
                let vm = this;
                let source = this[type];
                if (source === undefined || source === null) {
                    source = this.icm_icon;
                }
//                console.log(source);
                vm.picker=$('#' + this.getId(this.info.name)).fontIconPicker({
                    source: source,
                    iconsPerPage: 25
                }).on('change', function () {
                    let value = $(this).val();
                    vm.input_value=value;
//                    vm.$emit('input', value);
                });
            }
        },
        mounted(){
            this.initFontPicker(this.icon_type);
        }
    }
